dunes-script-module.script:
pathUri = "/blueprint/api/blueprints?name=" + blueprintName
var content = System.getModule("com.terasky.utils").vraGetOperation(pathUri);
content = JSON.parse(content)
if(content.numberOfElements == 1)
{
    return content.content[0].id
}
else
{
    for each(var blueprint in content.content)
    {
        if(blueprint.name.toLowerCase() == blueprintName.toLowerCase())
        {
            return blueprint.id
        }
    }
}
throw "Unable to retrieve blueprint name"
