dunes-script-module.script:
pathUri = "/catalog/api/admin/items?name=" + catalogItemName
var content = System.getModule("com.terasky.utils").vraGetOperation(pathUri);
content = JSON.parse(content)
if(content.numberOfElements == 1)
{
    return content.content[0].id
}
else
{
    for each(var catalog in content.content)
    {
        if(catalog.name.toLowerCase() == catalogItemName.toLowerCase())
        {
            return catalog.id
        }
    }
}
throw "Unable to retrieve catalog ID"
