dunes-script-module.script:
pathUri = "/deployment/api/deployments/" + deploymentId + "/requests"
var content = System.getModule("com.terasky.utils").vraGetOperation(pathUri);
content = JSON.parse(content)
if(content.content[0].name == "Create")
{
    return content.content[0].id
}
else
{
    for each(var request in content.content)
    {
        if(request.name == "Create")
        {
            return request.id
        }
    }
}

throw "Unable to retrieve creation request id"
