dunes-script-module.script:
// replacing new lines with a \n string in order to "escape" the YAML
var escapedYaml = yamlText.replace(/\n/g, "\\n")
                          .replace(/\'/g, "\\'")
                          .replace(/\"/g, '\\"')
                          .replace(/\r/g, "\\r")
                          .replace(/\//g, "\\/")
                          .replace(/\\ /g, "\\\\ ");
System.log(escapedYaml)
return escapedYaml;
